import { Component, type OnInit } from '@angular/core';
import { type FahrzeugForm, toFahrzeug } from './fahrzeugForm';
// eslint-disable-next-line @typescript-eslint/consistent-type-imports
import { FahrzeugWriteService, SaveError } from '../shared';
import { HttpErrorResponse, HttpStatusCode } from '@angular/common/http';
import { first, tap } from 'rxjs/operators';
import { FormGroup } from '@angular/forms';
import { Router } from '@angular/router'; // eslint-disable-line @typescript-eslint/consistent-type-imports
import { Title } from '@angular/platform-browser'; // eslint-disable-line @typescript-eslint/consistent-type-imports
import log from 'loglevel';

/**
 * component for the tag <create-fahrzeug>
 */
@Component({
    selector: 'hs-create-fahrzeug',
    templateUrl: './create-fahrzeug.component.html',
})
export class CreateFahrzeugComponent implements OnInit {
    readonly createForm = new FormGroup({});

    showWarning = false;

    fertig = false;

    errorMsg: string | undefined = undefined;

    constructor(
        private readonly service: FahrzeugWriteService,
        private readonly router: Router,
        private readonly titleService: Title,
    ) {
        log.debug(
            'CreateFahrzeugComponent.constructor: Injizierter Router:',
            router,
        );
    }

    ngOnInit() {
        this.titleService.setTitle('Neues Fahrzeug');
    }

    /**
     * this method realizes the onSubmit event handler for a new Fahrzeug
     * @return false, to cancel the event
     */
    onSubmit() {
        if (this.createForm.invalid) {
            log.debug(
                'CreateFahrzeugComponent.onSave: Validierungsfehler',
                this.createForm,
            );
        }

        const fahrzeugForm = this.createForm.value as FahrzeugForm;
        const neuesFahrzeug = toFahrzeug(fahrzeugForm);
        log.debug(
            'CreateFahrzeugComponent.onSave: neuesFahrzeug=',
            neuesFahrzeug,
        );

        this.service
            .save(neuesFahrzeug)
            .pipe(
                first(),
                tap(result => this.#setProps(result)),
            )
            .subscribe({ next: () => this.#navigateHome() });
    }

    #setProps(result: SaveError | string) {
        if (result instanceof SaveError) {
            this.#handleError(result);
            return;
        }

        this.fertig = true;
        this.showWarning = false;
        this.errorMsg = undefined;

        const id = result;
        log.debug('CreateFahrzeugComponent.#setProps: id=', id);
    }

    #handleError(err: SaveError) {
        const { statuscode } = err;
        log.debug(
            `CreateFahrzeugComponent.#handleError: statuscode=${statuscode}, err=`,
            err,
        );

        switch (statuscode) {
            case HttpStatusCode.UnprocessableEntity: {
                const { cause } = err;
                // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment
                this.errorMsg =
                    cause instanceof HttpErrorResponse
                        ? cause.error
                        : JSON.stringify(cause);
                break;
            }

            case HttpStatusCode.TooManyRequests:
                this.errorMsg =
                    'Zu viele Anfragen. Bitte versuchen Sie es später noch einmal.';
                break;

            case HttpStatusCode.GatewayTimeout:
                this.errorMsg = 'Ein interner Fehler ist aufgetreten.';
                log.error('Laeuft der Appserver? Port-Forwarding?');
                break;

            default:
                this.errorMsg = 'Ein unbekannter Fehler ist aufgetreten.';
                break;
        }
    }

    async #navigateHome() {
        if (this.errorMsg === undefined) {
            log.debug('CreateFahrzeugComponent.#navigateHome: Navigation');
            await this.router.navigate(['/']);
        }
    }
}
<div class="w-75 d-flex flex-column align-items-center mx-auto mt-4 mb-4">
  <div class="clickable d-flex align-items-center mb-3">
    <i class="material-icons">local_shipping</i>
    <span class="ms-1">Neues Fahrzeug</span>
  </div>

  <div class="container">
    <form [formGroup]="createForm" role="form" (submit)="onSubmit()">
      <div class="row has-warning mb-4" *ngIf="showWarning">
        <div class="col col-12 form-control-feedback">
          <span class="alert alert-warning">
            <i class="material-icons">warning</i>
            <strong class="text-help ms-1">
              Wenn Sie die Seite jetzt verlassen, verlieren Sie Ihre
              eingegebenen Daten.
            </strong>
          </span>
        </div>
      </div>

      <hs-create-kennzeichen [createForm]="createForm"></hs-create-kennzeichen>
      <hs-create-beschreibung
        [createForm]="createForm"
      ></hs-create-beschreibung>
      <hs-create-fahrzeugtyp [createForm]="createForm"></hs-create-fahrzeugtyp>
      <hs-create-erstzulassung
        [createForm]="createForm"
      ></hs-create-erstzulassung>
      <hs-create-kilometerstand
        [createForm]="createForm"
      ></hs-create-kilometerstand>
      <hs-create-fahrzeughalter
        [createForm]="createForm"
      ></hs-create-fahrzeughalter>

      <div class="row">
        <div class="col offset-2 gy-3">
          <button
            type="submit"
            class="btn .btn-tertiary d-flex align-items-center"
            [disabled]="createForm.invalid"
          >
            <i class="material-icons">check</i>
            <span class="ms-1">Jetzt anlegen</span>
          </button>
        </div>
      </div>
    </form>
    <small>* sind Pflichtfelder</small>
  </div>
</div>
<div class="mt-3">
  <hs-error-message [text]="errorMsg"></hs-error-message>
</div>
